        #region Classes de travail
        private class Zones : Dictionary<(int,int), Zone>
        {
            private Zone Depart { get; set; }
            private Zone Arrivee { get; set; }
            private char[,] Lignes { get; set; }
            public Zones(List<string> InputData, bool Pt2 = false)
            {
                List<Zone> ZonesTmp = new List<Zone>();
                long Id = 0;

                int nbLignes = InputData.Count;
                int Cols = InputData[0].Length;
                Lignes = new char[nbLignes, Cols];
                for (int y = 0; y < InputData.Count; y++)
                {
                    for(int x = 0; x < InputData[y].Length; x++)
                    {
                        Lignes[y, x] = InputData[y][x];
                        if (InputData[y][x] != '#')
                        {
                            Zone z = new Zone(x, y, (Pt2 ? '.' : InputData[y][x]), ++Id);
                            this.Add((x,y), z);
                            ZonesTmp.Add(z);
                        }
                    }
                }
                Depart = ZonesTmp.First();
                Arrivee = ZonesTmp.Last();
                foreach (Zone z in ZonesTmp)
                {
                    Zone v;
                    if (z.Char == '.' || z.Char == '>')
                    {
                        if (this.TryGetValue((z.X + 1, z.Y), out v))
                        {
                            z.Voisins.Add(v);
                        }
                    }
                    if (z.Char == '.' || z.Char == 'v')
                    {
                        if (this.TryGetValue((z.X, z.Y + 1), out v))
                        {
                            z.Voisins.Add(v);
                        }
                    }
                    if (z.Char == '.' || z.Char == '<')
                    {
                        if (this.TryGetValue((z.X - 1, z.Y), out v))
                        {
                            z.Voisins.Add(v);
                        }
                    }
                    if (z.Char == '.' || z.Char == '^')
                    {
                        if (this.TryGetValue((z.X, z.Y - 1), out v))
                        {
                            z.Voisins.Add(v);
                        }
                    }
                }

                this.Grouper();
            }

            private void Grouper()
            {
                List<Zone> ZonesMultiples = this.Where(z=>z.Value.Voisins.Count > 2).Select(z=>z.Value).ToList();
                ZonesMultiples.Add(Depart);
                ZonesMultiples.Add(Arrivee);

                List<long> fins = ZonesMultiples.Select(zm=>zm.Id).ToList();
                foreach(var z in ZonesMultiples)
                {
                    foreach(var v in z.Voisins)
                    {
                        var r = v.NextGroupe(z, 1, fins);
                        if(r.HasValue)
                        {
                            z.VoisinsGroupes.Add(r.Value);
                        }
                    }
                }
            }

            public long GetCheminLong()
            {
                var Chemins = Depart.GetChemins(new List<Zone>(), 0);

                Chemins.RemoveAll(c => !c.Exists(it=>it.Item1.Id == this.Arrivee.Id));


                return Chemins.Max(c => c.Max(iterator=>iterator.Item2));
            }

            public override string ToString()
            {
                List<string> strings = new List<string>();
                for (int i = 0; i < Lignes.GetLength(0); i++)
                {
                    string s = "";
                    for (int j = 0; j < Lignes.GetLength(1); j++)
                    {
                        s += Lignes[i, j];
                    }
                    strings.Add(s);
                }
                return string.Join("\r\n", strings);
            }
        }

        private class Zone
        {
            public int X { get; private set; }
            public int Y { get; private set; }
            public long Id { get; private set; }
            public char Char { get; private set; }
            public List<Zone> Voisins { get; private set; } = new List<Zone>();
            public List<(Zone, long)> VoisinsGroupes { get; private set; } = new List<(Zone, long)>();
            public Zone(int x, int y, char @char, long id)
            {
                X = x;
                Y = y;
                Char = @char;
                Id = id;
            }

            public List<List<(Zone, long)>> GetChemins(List<Zone> DejaPasses, long nombre)
            {
                List<List<(Zone, long)>> Res = new List<List<(Zone, long)>>();
                DejaPasses.Add(this);
                List<(Zone, long)> vs = VoisinsGroupes.Where(v => !DejaPasses.Contains(v.Item1)).ToList();
                if (vs.Count > 0)
                {
                    foreach (var v in vs)
                    {
                        Res.AddRange(v.Item1.GetChemins(DejaPasses.ToList(), nombre + v.Item2));
                    }
                }
                else
                {
                    Res.Add(new List<(Zone, long)>() { (this, nombre) });
                }
                return Res;
            }

            public (Zone, long)? NextGroupe(Zone prec, long nombre, List<long> Fins)
            {
                if(Fins.Contains(this.Id))
                {
                    return (this, nombre);
                }
                else
                {
                    var n = Voisins.FirstOrDefault(v => v != prec);
                    if (n == null)
                        return null;
                    return n.NextGroupe(this, nombre + 1, Fins);
                }
            }
        }
        #endregion
    }
